      rating:4,
      posterUrl:'', alt:'pic'
    },
    {
      title:'Venom',
      description :'2018 ‧ Action/SF ‧ 1h 52m',
      rating:4,
      posterUrl:'https://upload.wikimedia.org/wikipedia/en/1/10/Venom_%282018_film%29_poster.png',
      alt:'pic'
    },
    {
      title:'Spider-man: No way home',
      description :'2021 ‧ Action/SF ‧ 2h 28m',
      rating:5,
      posterUrl:'https://upload.wikimedia.org/wikipedia/en/thumb/7/70/Spider-Man_No_Way_Home_%E2%80%93_The_More_Fun_Stuff_Version_poster.jpeg/210px-Spider-Man_No_Way_Home_%E2%80%93_The_More_Fun_Stuff_Version_poster.jpeg',
      alt:'pic'
    },
    {
      title:'One piece',
      description :'Lorem',
      rating:4,
      posterUrl:'https://dnm.nflximg.net/api/v6/2DuQlx0fM4wd1nzqm5BFBi6ILa8/AAAAQfOO_2CUNrBbD6ZqpzaczOc69ZiUc9lmGb-HDMYOCCamtFesr2mt66xYkOa7gj7rML3zHArPoJiRdm1uUX6qGQF6wDhw56bjCzWRrAPf4rOtoVXKzcTXNuph6N5IuG4iP035pbohFGrTQyhtKXDEL30d.jpg?r=409',
      alt:'pic'
    },
    {
      title:'Iron-man ',
      description :'Lorem',
      rating:4,
      posterUrl:'url'
    },
    {
      title:'joker',
      description :'Lorem',
      rating:4,
      posterUrl:'url'
    },



  ])
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
