const handleShow = () => setShow(true);

const MovieList = ({movies}) => {
  return (
    <div className='grid-container'>
      <button onClick={handleShow}>Add</button>
       {movies.map((movie, index)=>(
        <MovieCard key={index} movie={movie}/> ))}
        <Add/>
    </div>
  )
}

export default MovieList
