import React, { useState } from 'react';

// MovieCard component to display individual movie details
const MovieCard = ({ movie }) => {
  return (
    <div className="movie-card">
      <img src={movie.posterURL} alt={movie.title} />
      <h3>{movie.title}</h3>
      <p>{movie.description}</p>
      <p>Rating: {movie.rating}</p>
    </div>
  );
};

// MovieList component to display a list of movies
const MovieList = ({ movies }) => {
  return (
    <div className="movie-list">
      {movies.map((movie, index) => (
        <MovieCard key={index} movie={movie} />
      ))}
    </div>
  );
};

// Filter component to filter movies based on title or rating
const Filter = ({ handleFilter }) => {
  const [title, setTitle] = useState('');
  const [rating, setRating] = useState('');

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleRatingChange = (event) => {
    setRating(event.target.value);
  };

  const applyFilter = () => {
    handleFilter({ title, rating });
  };

  return (
    <div className="filter">
      <input
        type="text"
        placeholder="Filter by Title"
        value={title}
        onChange={handleTitleChange}
      />
      <input
        type="number"
        placeholder="Filter by Rating"
        value={rating}
        onChange={handleRatingChange}
      />
      <button onClick={applyFilter}>Apply Filter</button>
    </div>
  );
};

// Main component that integrates MovieList, MovieCard, and Filter components
const MovieApp = () => {
  const [movies, setMovies] = useState([
    {
      title: 'Inception',
      description: 'A thief who steals corporate secrets through the use of dream-sharing technology.',
      posterURL: 'https://example.com/inception-poster.jpg',
      rating: 4.5,
    },
    // Other initial movies...
  ]);

  const [filteredMovies, setFilteredMovies] = useState(movies);

  const addMovie = (newMovie) => {
    setMovies([...movies, newMovie]);
  };

  const handleFilter = ({ title, rating }) => {
    let filtered = movies.filter((movie) => {
      let titleMatch = true;
      let ratingMatch = true;

      if (title && !movie.title.toLowerCase().includes(title.toLowerCase())) {
        titleMatch = false;
      }

      if (rating && movie.rating < rating) {
        ratingMatch = false;
      }

      return titleMatch && ratingMatch;
    });

    setFilteredMovies(filtered);
  };

  return (
    <div className="movie-app">
      <h1>Movie List</h1>
      <Filter handleFilter={handleFilter} />
      <MovieList movies={filteredMovies} />
      {/* You can add a form or button to add new movies */}
    </div>
  );
};

export default MovieApp;
